// src/App.js
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import axios from "axios";
import "./App.css";

// API configuration
const API_BASE_URL = "http://localhost:5000/api";

// Components
const Navbar = () => {
    return (
        <nav className="bg-blue-600 text-white p-4">
            <div className="container mx-auto flex justify-between items-center">
                <Link to="/" className="text-2xl font-bold">
                    Bluesky Analytics
                </Link>
                <div className="space-x-4">
                    <Link to="/" className="hover:underline">
                        Dashboard
                    </Link>
                    <Link to="/trending" className="hover:underline">
                        Trending
                    </Link>
                    <Link to="/user-analytics" className="hover:underline">
                        User Analytics
                    </Link>
                </div>
            </div>
        </nav>
    );
};

// Dashboard Page
const Dashboard = () => {
    const [recentPosts, setRecentPosts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get(
                    `${API_BASE_URL}/posts?limit=10`
                );
                setRecentPosts(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching recent posts:", error);
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-3xl font-bold mb-6">
                Bluesky Analytics Dashboard
            </h1>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="col-span-2">
                    <div className="bg-white rounded-lg shadow p-6">
                        <h2 className="text-xl font-semibold mb-4">
                            Recent Posts
                        </h2>
                        {loading ? (
                            <p>Loading posts...</p>
                        ) : (
                            <div className="space-y-4">
                                {recentPosts.map((post) => (
                                    <div
                                        key={post.post_id}
                                        className="border-b pb-4"
                                    >
                                        <div className="flex items-center mb-2">
                                            <img
                                                src={post.avatar}
                                                alt={`${post.author_display_name}'s avatar`}
                                                onError={(e) => {
                                                    e.target.src =
                                                        "https://bsky.social/images/default-avatar.png";
                                                }}
                                                className="w-10 h-10 rounded-full object-cover mr-3"
                                            />
                                            {/* <div className="w-10 h-10 rounded-full bg-gray-200 mr-3"></div> */}
                                            <div>
                                                <p className="font-semibold">
                                                    {post.author_display_name}
                                                </p>
                                                <p className="text-sm text-gray-500">
                                                    @{post.author}
                                                </p>
                                            </div>
                                        </div>
                                        <p className="mb-2">{post.content}</p>
                                        <div className="text-sm text-gray-500 flex space-x-4">
                                            <span>‚ù§Ô∏è {post.like_count}</span>
                                            <span>üîÅ {post.repost_count}</span>
                                            <span>üí¨ {post.reply_count}</span>
                                            <span>
                                                {new Date(
                                                    post.created_at
                                                ).toLocaleDateString()}
                                            </span>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>

                <div>
                    <div className="bg-white rounded-lg shadow p-6 mb-6">
                        <h2 className="text-xl font-semibold mb-4">
                            Quick Stats
                        </h2>
                        <div className="space-y-4">
                            <div className="bg-blue-50 p-4 rounded">
                                <p className="text-sm text-gray-500">
                                    Posts Collected
                                </p>
                                <p className="text-2xl font-bold">
                                    {recentPosts.length}
                                </p>
                            </div>
                            <div className="bg-green-50 p-4 rounded">
                                <p className="text-sm text-gray-500">
                                    Total Engagement
                                </p>
                                <p className="text-2xl font-bold">
                                    {recentPosts.reduce(
                                        (sum, post) =>
                                            sum +
                                            post.like_count +
                                            post.repost_count +
                                            post.reply_count,
                                        0
                                    )}
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

// Trending Page
const TrendingPage = () => {
    const [trending, setTrending] = useState([]);
    const [timeframe, setTimeframe] = useState(7);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTrending = async () => {
            try {
                setLoading(true);
                const response = await axios.get(
                    `${API_BASE_URL}/trending?days=${timeframe}`
                );
                setTrending(response.data);
                setLoading(false);
            } catch (error) {
                console.error("Error fetching trending hashtags:", error);
                setLoading(false);
            }
        };

        fetchTrending();
    }, [timeframe]);

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-3xl font-bold mb-6">Trending Hashtags</h1>

            <div className="mb-6">
                <label className="block text-sm font-medium text-gray-700 mb-2">
                    Timeframe
                </label>
                <select
                    value={timeframe}
                    onChange={(e) => setTimeframe(Number(e.target.value))}
                    className="border rounded p-2 w-full md:w-1/4"
                >
                    <option value={1}>Last 24 hours</option>
                    <option value={3}>Last 3 days</option>
                    <option value={7}>Last 7 days</option>
                    <option value={30}>Last 30 days</option>
                </select>
            </div>

            <div className="bg-white rounded-lg shadow overflow-hidden">
                {loading ? (
                    <div className="p-6">Loading trending data...</div>
                ) : (
                    <>
                        <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-gray-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Rank
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Hashtag
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Count
                                    </th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {trending.map((tag, index) => (
                                    <tr key={tag.hashtag}>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                            {index + 1}
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-blue-600">
                                            <Link
                                                to={`/posts?hashtag=${tag.hashtag}`}
                                            >
                                                #{tag.hashtag}
                                            </Link>
                                        </td>
                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                            {tag.total_count}
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>

                        {trending.length === 0 && (
                            <div className="p-6 text-center text-gray-500">
                                No trending hashtags found for this timeframe.
                            </div>
                        )}
                    </>
                )}
            </div>
        </div>
    );
};

// User Analytics Page
const UserAnalyticsPage = () => {
    const [username, setUsername] = useState("");
    const [analytics, setAnalytics] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!username) {
            setError("Please enter a username");
            return;
        }

        try {
            setLoading(true);
            setError("");
            const response = await axios.get(
                `${API_BASE_URL}/analytics/user/${username}`
            );
            setAnalytics(response.data);
            setLoading(false);
        } catch (error) {
            console.error("Error fetching user analytics:", error);
            setError("Failed to fetch user analytics");
            setLoading(false);
        }
    };

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-3xl font-bold mb-6">User Analytics</h1>

            <div className="bg-white rounded-lg shadow p-6 mb-6">
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label className="block text-sm font-medium text-gray-700 mb-2">
                            Bluesky Username
                        </label>
                        <div className="flex">
                            <span className="inline-flex items-center px-3 rounded-l-md border border-r-0 border-gray-300 bg-gray-50 text-gray-500">
                                @
                            </span>
                            <input
                                type="text"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                className="flex-1 min-w-0 block w-full px-3 py-2 rounded-none rounded-r-md border focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                                placeholder="username"
                            />
                        </div>
                    </div>
                    <button
                        type="submit"
                        className="bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded"
                        disabled={loading}
                    >
                        {loading ? "Loading..." : "Get Analytics"}
                    </button>
                    {error && <p className="mt-2 text-red-600">{error}</p>}
                </form>
            </div>

            {analytics && (
                <div className="bg-white rounded-lg shadow p-6">
                    <h2 className="text-xl font-semibold mb-4">
                        Analytics for @{analytics.username}
                    </h2>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
                        <div className="bg-blue-50 p-4 rounded">
                            <p className="text-sm text-gray-500">Total Posts</p>
                            <p className="text-2xl font-bold">
                                {analytics.post_count}
                            </p>
                        </div>
                        <div className="bg-green-50 p-4 rounded">
                            <p className="text-sm text-gray-500">Total Likes</p>
                            <p className="text-2xl font-bold">
                                {analytics.engagement.total_likes || 0}
                            </p>
                        </div>
                        <div className="bg-purple-50 p-4 rounded">
                            <p className="text-sm text-gray-500">
                                Total Reposts
                            </p>
                            <p className="text-2xl font-bold">
                                {analytics.engagement.total_reposts || 0}
                            </p>
                        </div>
                    </div>

                    <div className="mb-6">
                        <h3 className="text-lg font-semibold mb-2">
                            Top Hashtags
                        </h3>
                        {analytics.top_hashtags &&
                        analytics.top_hashtags.length > 0 ? (
                            <div className="bg-gray-50 p-4 rounded">
                                <ul className="space-y-2">
                                    {analytics.top_hashtags.map((tag) => (
                                        <li
                                            key={tag.hashtag}
                                            className="flex justify-between"
                                        >
                                            <span className="text-blue-600">
                                                #{tag.hashtag}
                                            </span>
                                            <span className="text-gray-600">
                                                {tag.count} posts
                                            </span>
                                        </li>
                                    ))}
                                </ul>
                            </div>
                        ) : (
                            <p className="text-gray-500">No hashtags found.</p>
                        )}
                    </div>

                    <div>
                        <h3 className="text-lg font-semibold mb-2">
                            Engagement Summary
                        </h3>
                        <div className="bg-gray-50 p-4 rounded">
                            <p>
                                Average engagement per post:
                                <span className="font-semibold ml-2">
                                    {Math.round(
                                        (((analytics.engagement.total_likes ||
                                            0) +
                                            (analytics.engagement
                                                .total_reposts || 0) +
                                            (analytics.engagement
                                                .total_replies || 0)) /
                                            analytics.post_count) *
                                            10
                                    ) / 10 || 0}
                                </span>
                            </p>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

// Main App Component
function App() {
    return (
        <Router>
            <div className="min-h-screen bg-gray-100">
                <Navbar />
                <Routes>
                    <Route path="/" element={<Dashboard />} />
                    <Route path="/trending" element={<TrendingPage />} />
                    <Route
                        path="/user-analytics"
                        element={<UserAnalyticsPage />}
                    />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
